 // SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "./priceOracle/IPriceOracle.sol";

interface IStrategy {
    function invest(address[] memory tokens, uint[] memory USDTAmts) external;
    function withdrawPerc(uint sharePerc) external;
    function withdrawFromPool(uint pid, uint sharePerc) external returns (uint);
    function emergencyWithdraw() external;
    function getEachPoolInUSD() external view returns (address[] memory tokens, uint[] memory pools);
    function getAllPoolInUSD() external view returns (uint);
    function getCurrentTokenCompositionPerc() external view returns (address[] memory tokens, uint[] memory percentages);
    function getAPR() external view returns (uint);
}

interface IERC20UpgradeableExt is IERC20Upgradeable {
    function decimals() external view returns (uint8);
}

contract BNIVaultTest is ReentrancyGuardUpgradeable, PausableUpgradeable, OwnableUpgradeable {
    using SafeERC20Upgradeable for IERC20UpgradeableExt;

    IERC20UpgradeableExt public USDT;
    uint8 usdtDecimals;

    address public admin;
    IStrategy public strategy;
    IPriceOracle public priceOracle;

    event Deposit(address caller, uint amtDeposit, address tokenDeposit);
    event Withdraw(address caller, uint amtWithdraw, address tokenWithdraw, uint sharePerc);
    event Rebalance(uint pid, uint sharePerc, uint amount, address target);
    event Reinvest(uint amount);
    event SetAdminWallet(address oldAdmin, address newAdmin);

    modifier onlyOwnerOrAdmin {
        require(msg.sender == owner() || msg.sender == address(admin), "Only owner or admin");
        _;
    }

    function initialize(
        address _admin, address _strategy, address _priceOracle,
        address _USDT
    ) external initializer {
        __Ownable_init();

        strategy = IStrategy(_strategy);
        admin = _admin;
        priceOracle = IPriceOracle(_priceOracle);

        USDT = IERC20UpgradeableExt(_USDT);
        usdtDecimals = USDT.decimals();
        require(6 <= usdtDecimals, "USDT decimals must >= 6");

        USDT.safeApprove(address(strategy), type(uint).max);
    }

    function getChainID() public view returns (uint256 id) {
        assembly {
            id := chainid()
        }
    }

    /// @notice The length of array is based on token count. And the lengths should be same on the arraies.
    /// @param _USDTAmts amounts of USDT should be deposited to each pools. It's 6 decimals
    function deposit(
        address _account, address[] memory _tokens, uint[] memory _USDTAmts
    ) external onlyOwnerOrAdmin nonReentrant whenNotPaused {
        require(_account != address(0), "Invalid account");
        uint poolCnt = _tokens.length;
        require(poolCnt == _USDTAmts.length, "Not match array length");

        uint k = 10 ** (usdtDecimals - 6);
        uint USDTAmt;
        for (uint i = 0; i < poolCnt; i ++) {
            _USDTAmts[i] = _USDTAmts[i] * k;
            USDTAmt += _USDTAmts[i];
        }
        require(0 < USDTAmt, "Amounts must > 0");

        USDT.safeTransferFrom(_account, address(this), USDTAmt);

        strategy.invest(_tokens, _USDTAmts);
        emit Deposit(_account, USDTAmt, address(USDT));
    }

    /// @param _sharePerc percentage of assets which should be withdrawn. It's 18 decimals
    function withdrawPerc(address _account, uint _sharePerc) external onlyOwnerOrAdmin nonReentrant {
        require(_sharePerc > 0, "SharePerc must > 0");
        require(_sharePerc <= 1e18, "Over 100%");

        uint USDTAmt;
        if (!paused()) {
            strategy.withdrawPerc(_sharePerc);
            USDTAmt = USDT.balanceOf(address(this));
        } else {
            USDTAmt = USDT.balanceOf(address(this)) * _sharePerc / 1e18;
        }
        USDT.safeTransfer(_account, USDTAmt);
        emit Withdraw(_account, USDTAmt, address(USDT), _sharePerc);
    }

    function rebalance(uint _pid, uint _sharePerc, address _target) external onlyOwnerOrAdmin {
        uint USDTAmt = strategy.withdrawFromPool(_pid, _sharePerc);
        if (0 < USDTAmt) {
            address[] memory targets = new address[](1);
            targets[0] = _target;
            uint[] memory USDTAmts = new uint[](1);
            USDTAmts[0] = USDTAmt;
            strategy.invest(targets, USDTAmts);
            emit Rebalance(_pid, _sharePerc, USDTAmt, _target);
        }
    }

    function emergencyWithdraw() external onlyOwnerOrAdmin whenNotPaused {
        _pause();
        strategy.emergencyWithdraw();
    }

    function reinvest(address[] memory _tokens, uint[] memory _perc) external onlyOwnerOrAdmin whenPaused {
        uint poolCnt = _tokens.length;
        require(poolCnt == _perc.length, "Not match array length");

        _unpause();
        uint USDTAmt = USDT.balanceOf(address(this));
        if (0 < USDTAmt) {
            uint totalPerc;
            for (uint i = 0; i < poolCnt; i ++) {
                totalPerc = _perc[i];
            }

            uint[] memory USMTAmts = new uint[](poolCnt);
            for (uint i = 0; i < poolCnt; i ++) {
                USMTAmts[i] = _perc[i] * USDTAmt / totalPerc;
            }

            strategy.invest(_tokens, USMTAmts);
            emit Reinvest(USDTAmt);
        }
    }

    function setAdmin(address _admin) external onlyOwner {
        address oldAdmin = admin;
        admin = _admin;
        emit SetAdminWallet(oldAdmin, _admin);
    }

    /// @return the price of USDT in USD.
    function getUSDTPriceInUSD() public view returns(uint, uint8) {
        // return priceOracle.getAssetPrice(address(USDT));
        return (1e8, 8);
    }

    function getEachPoolInUSD() public view returns (uint[] memory chainIDs, address[] memory tokens, uint[] memory pools) {
        (tokens, pools) = strategy.getEachPoolInUSD();
        uint poolCnt = pools.length;
        uint chainID = getChainID();
        chainIDs = new uint[](poolCnt);
        for (uint i = 0; i < poolCnt; i ++) {
            chainIDs[i] = chainID;
        }

        uint USDTAmt = USDT.balanceOf(address(this));
        if(USDTAmt > 0 && poolCnt > 0) {
            (uint USDTPriceInUSD, uint8 USDTPriceDecimals) = getUSDTPriceInUSD();
            uint _pool = USDT.balanceOf(address(this)) * (10 ** (18-usdtDecimals)) * USDTPriceInUSD / (10 ** USDTPriceDecimals);
            pools[0] += _pool;
        }
        return (chainIDs, tokens, pools);
    }

    function getAllPoolInUSD() public view returns (uint) {
        if (paused()) {
            (uint USDTPriceInUSD, uint8 USDTPriceDecimals) = getUSDTPriceInUSD();
            return USDT.balanceOf(address(this)) * (10 ** (18-usdtDecimals)) * USDTPriceInUSD / (10 ** USDTPriceDecimals);
        } else {
            return strategy.getAllPoolInUSD();
        }
    }

    function getCurrentCompositionPerc() external view returns (address[] memory tokens, uint[] memory percentages) {
        return strategy.getCurrentTokenCompositionPerc();
    }

    function getAPR() external view returns (uint) {
        return strategy.getAPR();
    }
}

contracts/bni/BNIVault.sol.content:
 // SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "./priceOracle/IPriceOracle.sol";

interface IStrategy {
    function invest(address[] memory tokens, uint[] memory USDTAmts) external;
    function withdrawPerc(uint sharePerc) external;
    function withdrawFromPool(uint pid, uint sharePerc) external returns (uint);
    function emergencyWithdraw() external;
    function getEachPoolInUSD() external view returns (address[] memory tokens, uint[] memory pools);
    function getAllPoolInUSD() external view returns (uint);
    function getCurrentTokenCompositionPerc() external view returns (address[] memory tokens, uint[] memory percentages);
    function getAPR() external view returns (uint);
}

interface IERC20UpgradeableExt is IERC20Upgradeable {
    function decimals() external view returns (uint8);
}

contract BNIVault is ReentrancyGuardUpgradeable, PausableUpgradeable, OwnableUpgradeable {
    using SafeERC20Upgradeable for IERC20UpgradeableExt;

    IERC20UpgradeableExt public USDT;
    uint8 usdtDecimals;

    address public admin;
    IStrategy public strategy;
    IPriceOracle public priceOracle;

    uint constant DENOMINATOR = 10000;
    uint public profitFeePerc;
    address public treasuryWallet;
    uint public watermark; // In USD (18 decimals)
    uint public fees; // In USD (18 decimals)

    event Deposit(address caller, uint amtDeposit, address tokenDeposit);
    event Withdraw(address caller, uint amtWithdraw, address tokenWithdraw, uint sharePerc);
    event Rebalance(uint pid, uint sharePerc, uint amount, address target);
    event Reinvest(uint amount);
    event SetTreasuryWallet(address oldTreasuryWallet, address newTreasuryWallet);
    event SetAdminWallet(address oldAdmin, address newAdmin);
    event CollectProfitAndUpdateWatermark(uint currentWatermark, uint lastWatermark, uint fee);
    event AdjustWatermark(uint currentWatermark, uint lastWatermark);
    event TransferredOutFees(uint fees, address token);

    modifier onlyOwnerOrAdmin {
        require(msg.sender == owner() || msg.sender == address(admin), "Only owner or admin");
        _;
    }

    function initialize(
        address _treasuryWallet, address _admin,
        address _strategy, address _priceOracle,
        address _USDT
    ) external initializer {
        __Ownable_init();

        treasuryWallet = _treasuryWallet;
        admin = _admin;
        strategy = IStrategy(_strategy);
        priceOracle = IPriceOracle(_priceOracle);

        profitFeePerc = 2000;

        USDT = IERC20UpgradeableExt(_USDT);
        usdtDecimals = USDT.decimals();
        require(6 <= usdtDecimals, "USDT decimals must >= 6");

        USDT.safeApprove(address(strategy), type(uint).max);
    }

    function getChainID() public view returns (uint256 id) {
        assembly {
            id := chainid()
        }
    }

    /// @notice The length of array is based on token count. And the lengths should be same on the arraies.
    /// @param _USDTAmts amounts of USDT should be deposited to each pools. It's 6 decimals
    function deposit(
        address _account, address[] memory _tokens, uint[] memory _USDTAmts
    ) external onlyOwnerOrAdmin nonReentrant whenNotPaused {
        require(_account != address(0), "Invalid account");
        uint poolCnt = _tokens.length;
        require(poolCnt == _USDTAmts.length, "Not match array length");

        uint k = 10 ** (usdtDecimals - 6);
        uint USDTAmt;
        for (uint i = 0; i < poolCnt; i ++) {
            _USDTAmts[i] = _USDTAmts[i] * k;
            USDTAmt += _USDTAmts[i];
        }
        require(0 < USDTAmt, "Amounts must > 0");

        USDT.safeTransferFrom(_account, address(this), USDTAmt);

        (uint USDTPriceInUSD, uint8 USDTPriceDecimals) = getUSDTPriceInUSD();
        uint amtDeposit = USDTAmt * (10 ** (18-usdtDecimals)) * USDTPriceInUSD / (10 ** USDTPriceDecimals);

        if (watermark > 0) _collectProfitAndUpdateWatermark();
        (uint newUSDTAmt, uint[] memory newUSDTAmts) = _transferOutFees(USDTAmt, _USDTAmts);
        if (newUSDTAmt > 0) {
            strategy.invest(_tokens, newUSDTAmts);
        }
        adjustWatermark(amtDeposit, true);

        emit Deposit(_account, USDTAmt, address(USDT));
    }

    /// @param _sharePerc percentage of assets which should be withdrawn. It's 18 decimals
    function withdrawPerc(address _account, uint _sharePerc) external onlyOwnerOrAdmin nonReentrant {
        require(_sharePerc > 0, "SharePerc must > 0");
        require(_sharePerc <= 1e18, "Over 100%");

        uint pool = getAllPoolInUSD();
        uint withdrawAmt = pool * _sharePerc / 1e18;
        uint sharePerc = withdrawAmt * 1e18 / (pool + fees);
        uint USDTAmt;
        if (!paused()) {
            strategy.withdrawPerc(sharePerc);
            USDTAmt = USDT.balanceOf(address(this));
            adjustWatermark(withdrawAmt, false);
        } else {
            USDTAmt = USDT.balanceOf(address(this)) * sharePerc / 1e18;
        }
        USDT.safeTransfer(_account, USDTAmt);
        emit Withdraw(_account, withdrawAmt, address(USDT), _sharePerc);
    }

    function rebalance(uint _pid, uint _sharePerc, address _target) external onlyOwnerOrAdmin {
        uint USDTAmt = strategy.withdrawFromPool(_pid, _sharePerc);
        if (0 < USDTAmt) {
            address[] memory targets = new address[](1);
            targets[0] = _target;
            uint[] memory USDTAmts = new uint[](1);
            USDTAmts[0] = USDTAmt;
            strategy.invest(targets, USDTAmts);
            emit Rebalance(_pid, _sharePerc, USDTAmt, _target);
        }
    }

    function emergencyWithdraw() external onlyOwnerOrAdmin whenNotPaused {
        _pause();
        strategy.emergencyWithdraw();
        watermark = 0;
    }

    function reinvest(address[] memory _tokens, uint[] memory _perc) external onlyOwnerOrAdmin whenPaused {
        uint poolCnt = _tokens.length;
        require(poolCnt == _perc.length, "Not match array length");

        _unpause();
        uint USDTAmt = USDT.balanceOf(address(this));
        if (0 < USDTAmt) {
            (uint USDTPriceInUSD, uint8 USDTPriceDecimals) = getUSDTPriceInUSD();
            uint amtDeposit = USDTAmt * (10 ** (18-usdtDecimals)) * USDTPriceInUSD / (10 ** USDTPriceDecimals);
            uint totalPerc;
            for (uint i = 0; i < poolCnt; i ++) {
                totalPerc = _perc[i];
            }

            uint[] memory USMTAmts = new uint[](poolCnt);
            for (uint i = 0; i < poolCnt; i ++) {
                USMTAmts[i] = _perc[i] * USDTAmt / totalPerc;
            }

            strategy.invest(_tokens, USMTAmts);
            adjustWatermark(amtDeposit, true);
            emit Reinvest(USDTAmt);
        }
    }

    function collectProfitAndUpdateWatermark() external onlyOwnerOrAdmin whenNotPaused {
        _collectProfitAndUpdateWatermark();
    }
    function _collectProfitAndUpdateWatermark() private {
        uint currentWatermark = strategy.getAllPoolInUSD();
        uint lastWatermark = watermark;
        uint fee;
        if (currentWatermark > lastWatermark) {
            uint profit = currentWatermark - lastWatermark;
            fee = profit * profitFeePerc / DENOMINATOR;
            fees += fee;
            watermark = currentWatermark;
        }
        emit CollectProfitAndUpdateWatermark(currentWatermark, lastWatermark, fee);
    }

    /// @param signs True for positive, false for negative
    function adjustWatermark(uint amount, bool signs) private {
        uint lastWatermark = watermark;
        watermark = signs == true
                    ? watermark + amount
                    : (watermark > amount) ? watermark - amount : 0;
        emit AdjustWatermark(watermark, lastWatermark);
    }

    function withdrawFees() external onlyOwnerOrAdmin {
        if (!paused()) {
            uint pool = strategy.getAllPoolInUSD();
            uint _fees = fees;
            uint sharePerc = _fees < pool ? _fees * 1e18 / pool : 1e18;
            strategy.withdrawPerc(sharePerc);
        }
        _transferOutFees(USDT.balanceOf(address(this)), new uint[](0));
    }

    function _transferOutFees(uint _USDTAmt, uint[] memory _USDTAmts) private returns (uint, uint[] memory) {
        uint _fees = fees;
        if (_fees != 0) {
            (uint USDTPriceInUSD, uint8 USDTPriceDecimals) = getUSDTPriceInUSD();
            uint FeeAmt = _fees * (10 ** USDTPriceDecimals) / ((10 ** (18-usdtDecimals)) * USDTPriceInUSD);

            uint prevUSDTAmt = _USDTAmt;
            uint poolCnt = _USDTAmts.length;
            if (FeeAmt < _USDTAmt) {
                _fees = 0;
                _USDTAmt -= FeeAmt;
            } else {
                _fees -= (_USDTAmt * (10 ** (18-usdtDecimals)) * USDTPriceInUSD / (10 ** USDTPriceDecimals));
                FeeAmt = _USDTAmt;
                _USDTAmt = 0;
            }
            fees = _fees;

            for (uint i = 0; i < poolCnt; i ++) {
                _USDTAmts[i] = _USDTAmts[i] * _USDTAmt / prevUSDTAmt;
            }

            USDT.safeTransfer(treasuryWallet, FeeAmt);
            emit TransferredOutFees(FeeAmt, address(USDT)); // Decimal follow _token
        }
        return (_USDTAmt, _USDTAmts);
    }

    function setStrategy(address _strategy) external onlyOwner {
        strategy = IStrategy(_strategy);

        if (USDT.allowance(address(this), address(strategy)) == 0) {
            USDT.safeApprove(address(strategy), type(uint).max);
        }
    }

    function setProfitFeePerc(uint _profitFeePerc) external onlyOwner {
        require(profitFeePerc < 3001, "Profit fee cannot > 30%");
        profitFeePerc = _profitFeePerc;
    }

    function setTreasuryWallet(address _treasuryWallet) external onlyOwner {
        address oldTreasuryWallet = treasuryWallet;
        treasuryWallet = _treasuryWallet;
        emit SetTreasuryWallet(oldTreasuryWallet, _treasuryWallet);
    }

    function setAdmin(address _admin) external onlyOwner {
        address oldAdmin = admin;
        admin = _admin;
        emit SetAdminWallet(oldAdmin, _admin);
    }

    /// @return the price of USDT in USD.
    function getUSDTPriceInUSD() public view returns(uint, uint8) {
        return priceOracle.getAssetPrice(address(USDT));
    }

    function getEachPoolInUSD() public view returns (uint[] memory chainIDs, address[] memory tokens, uint[] memory pools) {
        (tokens, pools) = strategy.getEachPoolInUSD();
        uint poolCnt = pools.length;
        uint chainID = getChainID();
        chainIDs = new uint[](poolCnt);
        for (uint i = 0; i < poolCnt; i ++) {
            chainIDs[i] = chainID;
        }

        uint USDTAmt = USDT.balanceOf(address(this));
        if(USDTAmt > 0 && poolCnt > 0) {
            (uint USDTPriceInUSD, uint8 USDTPriceDecimals) = getUSDTPriceInUSD();
            uint _pool = USDT.balanceOf(address(this)) * (10 ** (18-usdtDecimals)) * USDTPriceInUSD / (10 ** USDTPriceDecimals);
            pools[0] += _pool;
        }
        return (chainIDs, tokens, pools);
    }

    function getAllPoolInUSD() public view returns (uint) {
        uint pool;
        if (paused()) {
            (uint USDTPriceInUSD, uint8 USDTPriceDecimals) = getUSDTPriceInUSD();
            pool = USDT.balanceOf(address(this)) * (10 ** (18-usdtDecimals)) * USDTPriceInUSD / (10 ** USDTPriceDecimals);
        } else {
            pool = strategy.getAllPoolInUSD();
        }
        return (pool > fees ? pool - fees : 0);
    }

    function getCurrentCompositionPerc() external view returns (address[] memory tokens, uint[] memory percentages) {
        return strategy.getCurrentTokenCompositionPerc();
    }

    function getAPR() external view returns (uint) {
        return strategy.getAPR();
    }
}

contracts/bni/BNIMinterTest.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import "./priceOracle/IPriceOracle.sol";
import "./constant/AvaxConstant.sol";
import "./constant/AuroraConstant.sol";
import "./constant/MaticConstant.sol";

interface IBNI is IERC20Upgradeable {
    function decimals() external view returns (uint8);
    function mint(address account_, uint256 amount_) external;
    function burn(uint256 amount) external;
    function burnFrom(address account_, uint256 amount_) external;
}

error OffchainLookup(address sender, string[] urls, bytes callData, bytes4 callbackFunction, bytes extraData);

interface Gateway {
    function getCurrentTokenCompositionPerc1() external view returns(
        uint[] memory _chainIDs, address[] memory _tokens, uint[] memory _poolInUSDs,
        bytes memory sig
    );
    function getAllPoolInUSD1() external view returns(
        uint[] memory _allPoolInUSDs,
        bytes memory sig
    );
    function getPricePerFullShare1() external view returns(
        uint[] memory _allPoolInUSDs,
        bytes memory sig
    );
    function getAPR1() external view returns(
        uint[] memory _allPoolInUSDs,  uint[] memory _APRs,
        bytes memory sig
    );
    function getDepositTokenComposition1() external view returns(
        uint[] memory _chainIDs, address[] memory _tokens, uint[] memory _poolInUSDs,
        bytes memory sig
    );
}


contract BNIMinterTest is ReentrancyGuardUpgradeable, PausableUpgradeable, OwnableUpgradeable {
    using ECDSAUpgradeable for bytes32;

    uint constant DENOMINATOR = 10000;

    uint[] public chainIDs;
    address[] public tokens;
    uint[] public targetPercentages;
    mapping(uint => mapping(address => uint)) public tid; // Token indices in arrays

    address public admin;
    IBNI public BNI;
    IPriceOracle public priceOracle;

    string[] public urls;

    event SetAdminWallet(address oldAdmin, address newAdmin);
    event AddToken(uint chainID, address token, uint tid);
    event RemoveToken(uint chainID, address token, uint targetPerc, uint tid);
    event Mint(address caller, uint amtDeposit, uint shareMinted);
    event Burn(address caller, uint shareBurned);

    modifier onlyOwnerOrAdmin {
        require(msg.sender == owner() || msg.sender == address(admin), "Only owner or admin");
        _;
    }

    function initialize(
        address _admin, address _BNI, address _priceOracle
    ) external initializer {
        __Ownable_init();

        admin = _admin;
        BNI = IBNI(_BNI);
        priceOracle = IPriceOracle(_priceOracle);

        chainIDs.push(MaticConstant.CHAINID);
        chainIDs.push(AvaxConstant.CHAINID);
        chainIDs.push(AuroraConstant.CHAINID);

        tokens.push(MaticConstant.WMATIC);
        tokens.push(AvaxConstant.WAVAX);
        tokens.push(AuroraConstant.WNEAR);

        targetPercentages.push(4000); // 40%
        targetPercentages.push(4000); // 40%
        targetPercentages.push(2000); // 20%

        updateTid();

        urls.push("http://localhost:8000/");
    }

    function updateTid() private {
        uint[] memory _chainIDs = chainIDs;
        address[] memory _tokens = tokens;

        uint tokenCnt = _tokens.length;
        for (uint i = 0; i < tokenCnt; i ++) {
            tid[_chainIDs[i]][_tokens[i]] = i;
        }
    }

    function setAdmin(address _admin) external onlyOwner {
        address oldAdmin = admin;
        admin = _admin;
        emit SetAdminWallet(oldAdmin, _admin);
    }

    /// @notice After this method called, setTokenCompositionTargetPerc should be called to adjust percentages.
    function addToken(uint _chainID, address _token) external onlyOwner {
        uint _tid = tid[_chainID][_token];
        require ((_tid == 0 && _chainID != chainIDs[0] && _token != tokens[0]), "Already added");

        chainIDs.push(_chainID);
        tokens.push(_token);
        targetPercentages.push(0);

        _tid = tokens.length-1;
        tid[_chainID][_token] = _tid;

        emit AddToken(_chainID, _token, _tid);
    }

    /// @notice After this method called, setTokenCompositionTargetPerc should be called to adjust percentages.
    function removeToken(uint _tid) external onlyOwner {
        uint tokenCnt = tokens.length;
        require(_tid < tokenCnt, "Invalid tid");

        uint _chainID = chainIDs[_tid];
        address _token = tokens[_tid];
        uint _targetPerc = targetPercentages[_tid];

        chainIDs[_tid] = chainIDs[tokenCnt-1];
        chainIDs.pop();
        tokens[_tid] = tokens[tokenCnt-1];
        tokens.pop();
        targetPercentages[_tid] = targetPercentages[tokenCnt-1];
        targetPercentages.pop();

        tid[_chainID][_token] = 0;
        updateTid();

        emit RemoveToken(_chainID, _token, _targetPerc, _tid);
    }

    /// @notice The length of array is based on token count.
    function setTokenCompositionTargetPerc(uint[] calldata _targetPerc) public onlyOwner {
        uint targetCnt = _targetPerc.length;
        require(targetCnt == targetPercentages.length, "Invalid count");

        uint sum;
        for (uint i = 0; i < targetCnt; i ++) {
            targetPercentages[i] = _targetPerc[i];
            sum += _targetPerc[i];
        }
        require(sum == DENOMINATOR, "Invalid parameter");
    }

    /// @notice The length of array is based on token count. And the lengths should be same on the arraies.
    function getEachPoolInUSD(
        uint[] memory _chainIDs, address[] memory _tokens, uint[] memory _poolInUSDs
    ) private view returns (uint[] memory pools) {
        uint inputCnt = _tokens.length;
        uint tokenCnt = tokens.length;
        pools = new uint[](tokenCnt);

        for (uint i = 0; i < inputCnt; i ++) {
            uint _chainID = _chainIDs[i];
            address _token = _tokens[i];
            uint _tid = tid[_chainID][_token];
            if (tokenCnt <= _tid) continue;
            if (_tid == 0 && (_chainID != chainIDs[0] || _token != tokens[0])) continue;

            pools[_tid] = _poolInUSDs[i];
        }
    }

    /// @notice The length of array is based on token count. And the lengths should be same on the arraies.
    function getCurrentTokenCompositionPerc(
        uint[] memory _chainIDs, address[] memory _tokens, uint[] memory _poolInUSDs
    ) public view returns (
        uint[] memory, address[] memory, uint[] memory pools, uint[] memory percentages
    ) {
        pools = getEachPoolInUSD(_chainIDs, _tokens, _poolInUSDs);
        uint poolCnt = pools.length;

        uint allPool;
        for (uint i = 0; i < poolCnt; i ++) {
            allPool += pools[i];
        }

        percentages = new uint[](poolCnt);
        for (uint i = 0; i < poolCnt; i ++) {
            percentages[i] = allPool == 0 ? targetPercentages[i] : pools[i] * DENOMINATOR / allPool;
        }

        return (chainIDs, tokens, pools, percentages);
    }
    function getCurrentTokenCompositionPerc1() external view returns (
        uint[] memory, address[] memory, uint[] memory, uint[] memory
    ) {
        revert OffchainLookup(address(this), urls,
            abi.encodeWithSelector(Gateway.getCurrentTokenCompositionPerc1.selector),
            BNIMinterTest.getCurrentTokenCompositionPercWithSig.selector,
            abi.encode(0)
        );
    }
    function getCurrentTokenCompositionPercWithSig(bytes calldata result, bytes calldata extraData) external view returns(
        uint[] memory, address[] memory, uint[] memory, uint[] memory
    ) {
        extraData;
        (uint[] memory _chainIDs, address[] memory _tokens, uint[] memory _poolInUSDs, bytes memory sig)
            = abi.decode(result, (uint[], address[], uint[], bytes));

        address recovered = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32",
            keccak256(abi.encodePacked(_chainIDs, _tokens, _poolInUSDs))
        )).recover(sig);
        require(admin == recovered, "Signer is incorrect");

        return getCurrentTokenCompositionPerc(_chainIDs, _tokens, _poolInUSDs);
    }

    /// @notice The length of array is based on network count. And the lengths should be same on the arraies.
    function getAllPoolInUSD(uint[] memory _allPoolInUSDs) public pure returns (uint) {
        uint networkCnt = _allPoolInUSDs.length;
        uint allPoolInUSD;
        for (uint i = 0; i < networkCnt; i ++) {
            allPoolInUSD += _allPoolInUSDs[i];
        }
        return allPoolInUSD;
    }
    function getAllPoolInUSD1() external view returns (uint) {
        revert OffchainLookup(address(this), urls,
            abi.encodeWithSelector(Gateway.getAllPoolInUSD1.selector),
            BNIMinterTest.getAllPoolInUSD1WithSig.selector,
            abi.encode(0)
        );
    }
    function getAllPoolInUSD1WithSig(bytes calldata result, bytes calldata extraData) external view returns(uint) {
        extraData;
        (uint[] memory _allPoolInUSDs, bytes memory sig) = abi.decode(result, (uint[], bytes));

        address recovered = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32",
            keccak256(abi.encodePacked(_allPoolInUSDs))
        )).recover(sig);
        require(admin == recovered, "Signer is incorrect");

        return getAllPoolInUSD(_allPoolInUSDs);
    }

    /// @notice Can be used for calculate both user shares & APR
    function getPricePerFullShare(uint[] memory _allPoolInUSDs) public view returns (uint) {
        uint _totalSupply = BNI.totalSupply();
        if (_totalSupply == 0) return 1e18;
        return getAllPoolInUSD(_allPoolInUSDs) * 1e18 / _totalSupply;
    }
    function getPricePerFullShare1() external view returns (uint) {
        revert OffchainLookup(address(this), urls,
            abi.encodeWithSelector(Gateway.getPricePerFullShare1.selector),
            BNIMinterTest.getPricePerFullShare1WithSig.selector,
            abi.encode(0)
        );
    }
    function getPricePerFullShare1WithSig(bytes calldata result, bytes calldata extraData) external view returns(uint) {
        extraData;
        (uint[] memory _allPoolInUSDs, bytes memory sig) = abi.decode(result, (uint[], bytes));

        address recovered = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32",
            keccak256(abi.encodePacked(_allPoolInUSDs))
        )).recover(sig);
        require(admin == recovered, "Signer is incorrect");

        return getPricePerFullShare(_allPoolInUSDs);
    }

    /// @notice The length of array is based on network count. And the lengths should be same on the arraies.
    function getAPR(uint[] memory _allPoolInUSDs, uint[] memory _APRs) public pure returns (uint) {
        uint networkCnt = _allPoolInUSDs.length;
        require(networkCnt == _APRs.length, "Not match array length");

        uint pool = getAllPoolInUSD(_allPoolInUSDs);
        if (pool == 0) return 0;

        uint allApr;
        for (uint i = 0; i < networkCnt; i ++) {
            allApr += (_APRs[i] * _allPoolInUSDs[i]);
        }
        return (allApr / pool);
    }
    function getAPR1() external view returns (uint) {
        revert OffchainLookup(address(this), urls,
            abi.encodeWithSelector(Gateway.getAPR1.selector),
            BNIMinterTest.getAPR1WithSig.selector,
            abi.encode(0)
        );
    }
    function getAPR1WithSig(bytes calldata result, bytes calldata extraData) external view returns(uint) {
        extraData;
        (uint[] memory _allPoolInUSDs,  uint[] memory _APRs, bytes memory sig) = abi.decode(result, (uint[], uint[], bytes));

        address recovered = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32",
            keccak256(abi.encodePacked(_allPoolInUSDs, _APRs))
        )).recover(sig);
        require(admin == recovered, "Signer is incorrect");

        return getAPR(_allPoolInUSDs, _APRs);
    }

    /// @return the price of USDT in USD.
    function getUSDTPriceInUSD() public view returns(uint, uint8) {
        // return priceOracle.getAssetPrice(AvaxConstant.USDT);
        return (1e8, 8);
    }

    /// @notice The length of array is based on token count. And the lengths should be same on the arraies.
    /// @param _USDTAmt amount of USDT with 6 decimals
    /// @return _USDTAmts amount of USDT should be deposited to each pools
    function getDepositTokenComposition(
        uint[] memory _chainIDs, address[] memory _tokens, uint[] memory _poolInUSDs, uint _USDTAmt
    ) public view returns (
        uint[] memory, address[] memory, uint[] memory _USDTAmts
    ) {
        (,, uint[] memory pools, uint[] memory perc) = getCurrentTokenCompositionPerc(_chainIDs, _tokens, _poolInUSDs);
        uint poolCnt = perc.length;
        (uint USDTPriceInUSD, uint8 USDTPriceDecimals) = getUSDTPriceInUSD();

        uint allPool = _USDTAmt * 1e12 * USDTPriceInUSD / (10 ** USDTPriceDecimals); // USDT's decimals is 6
        for (uint i = 0; i < poolCnt; i ++) {
            allPool += pools[i];
        }

        uint totalAllocation;
        uint[] memory allocations = new uint[](poolCnt);
        for (uint i = 0; i < poolCnt; i ++) {
            uint target = allPool * targetPercentages[i] / DENOMINATOR;
            if (pools[i] < target) {
                uint diff = target - pools[i];
                allocations[i] = diff;
                totalAllocation += diff;
            }
        }

        _USDTAmts = new uint[](poolCnt);
        for (uint i = 0; i < poolCnt; i ++) {
            _USDTAmts[i] = _USDTAmt * allocations[i] / totalAllocation;
        }

        return (chainIDs, tokens, _USDTAmts);
    }
    function getDepositTokenComposition1(uint _USDTAmt) external view returns (
        uint[] memory, address[] memory, uint[] memory
    ) {
        revert OffchainLookup(address(this), urls,
            abi.encodeWithSelector(Gateway.getDepositTokenComposition1.selector),
            BNIMinterTest.getDepositTokenComposition1WithSig.selector,
            abi.encode(_USDTAmt)
        );
    }
    function getDepositTokenComposition1WithSig(bytes calldata result, bytes calldata extraData) external view returns(
        uint[] memory, address[] memory, uint[] memory
    ) {
        (uint _USDTAmt) = abi.decode(extraData, (uint));
        (uint[] memory _chainIDs, address[] memory _tokens, uint[] memory _poolInUSDs, bytes memory sig)
            = abi.decode(result, (uint[], address[], uint[], bytes));

        address recovered = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32",
            keccak256(abi.encodePacked(_chainIDs, _tokens, _poolInUSDs))
        )).recover(sig);
        require(admin == recovered, "Signer is incorrect");

        return getDepositTokenComposition(_chainIDs, _tokens, _poolInUSDs, _USDTAmt);
    }

    /// @dev mint BNIs according to the deposited USDT
    /// @param _pool total USD worth in all pools of BNI after deposited
    /// @param _account account to which BNIs will be minted
    /// @param _USDTAmt the deposited amount of USDT with 6 decimals
    function mint(uint _pool, address _account, uint _USDTAmt) external onlyOwnerOrAdmin nonReentrant whenNotPaused {
        (uint USDTPriceInUSD, uint8 USDTPriceDecimals) = getUSDTPriceInUSD();
        uint amtDeposit = _USDTAmt * 1e12 * USDTPriceInUSD / (10 ** USDTPriceDecimals); // USDT's decimals is 6
        _pool = (amtDeposit < _pool) ? _pool - amtDeposit : 0;

        uint _totalSupply = BNI.totalSupply();
        uint share = (_totalSupply == 0 || _pool == 0)  ? amtDeposit : amtDeposit * _totalSupply / _pool;

        BNI.mint(_account, share);
        emit Mint(_account, amtDeposit, share);
    }

    /// @notice The length of array is based on token count. And the lengths should be same on the arraies.
    /// @param _share amount of BNI to be withdrawn
    /// @return _sharePerc percentage of assets which should be withdrawn. It's 18 decimals
    function getWithdrawPerc(address _account, uint _share) public view returns (uint _sharePerc) {
        require(0 < _share && _share <= BNI.balanceOf(_account), "Invalid share amount");
        return (_share * 1e18) / BNI.totalSupply();
    }

    /// @dev mint BNIs according to the deposited USDT
    /// @param _account account to which BNIs will be minted
    /// @param _share amount of BNI to be burnt
    function burn(address _account, uint _share) external onlyOwnerOrAdmin nonReentrant {
        require(0 < _share && _share <= BNI.balanceOf(_account), "Invalid share amount");
        BNI.burnFrom(_account, _share);
        emit Burn(_account, _share);
    }

    function setUrls(string[] memory _urls) external onlyOwner {
        urls = _urls;
    }
}

