// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "./BNIStrategy.sol";
import "../../../interfaces/IL2Vault.sol";

contract AvaxBNIStrategy is BNIStrategy {
    using SafeERC20Upgradeable for IERC20Upgradeable;

    IERC20Upgradeable public constant WAVAX = IERC20Upgradeable(0xB31f66AA3C1e785363F0875A1B74E27b85FD66c7);

    IL2Vault public WAVAXVault;

    event InvestWAVAX(uint USDTAmt, uint WAVAXAmt);
    event WithdrawWAVAX(uint WAVAXAmt, uint USDTAmt);

    function setWAVAXVault(IL2Vault _WAVAXVault) external onlyOwner {
        WAVAXVault = _WAVAXVault;
        if (WAVAX.allowance(address(this), address(WAVAXVault)) == 0) {
            WAVAX.safeApprove(address(WAVAXVault), type(uint).max);
        }
    }

    function _investWAVAX(uint USDTAmt) private {
        uint WAVAXAmt = WAVAX.balanceOf(address(this));
        if (WAVAXAmt > 0) {
            WAVAXVault.deposit(WAVAXAmt);
            emit InvestWAVAX(USDTAmt, WAVAXAmt);
        }
    }

    function _invest(uint[] memory _USDTAmts) internal virtual override {
        super._invest(_USDTAmts);

        uint poolCnt = _USDTAmts.length;
        for (uint i = 0; i < poolCnt; i ++) {
            address token = tokens[i];
            if (token == address(WAVAX)) {
                _investWAVAX(_USDTAmts[i]);
            }
        }
    }

    function _withdrawWAVAX(uint _sharePerc) private returns (uint USDTAmt) {
        uint amount = WAVAXVault.balanceOf(address(this)) * _sharePerc / 1e18;
        if (0 < amount) {
            WAVAXVault.withdraw(amount);
            uint WAVAXAmt = WAVAX.balanceOf(address(this));
            USDTAmt = _swapForUSDT(address(WAVAX), WAVAXAmt);
            emit WithdrawWAVAX(WAVAXAmt, USDTAmt);
        }
    }

    function _withdrawFromPool(uint _pid, uint _sharePerc) internal virtual override returns (uint USDTAmt) {
        address token = tokens[_pid];
        if (token == address(WAVAX)) {
            USDTAmt = _withdrawWAVAX(_sharePerc);
        } else {
            USDTAmt = super._withdrawFromPool(_pid, _sharePerc);
        }
    }

    function getWAVAXPoolInUSD() private view  returns (uint) {
        uint balance = WAVAXVault.balanceOf(address(this));
        return balance == 0 ? 0 : WAVAXVault.getAllPoolInUSD() * balance / WAVAXVault.totalSupply(); //to exclude L1 deposits from other addresses
    }

    function _getPoolInUSD(uint _pid) internal view virtual override returns (uint pool) {
        address token = tokens[_pid];
        if (token == address(WAVAX)) {
            pool = getWAVAXPoolInUSD();
        } else {
            pool = super._getPoolInUSD(_pid);
        }
    }

    function getAPR() public view override returns (uint) {
        (address[] memory _tokens, uint[] memory perc) = getCurrentTokenCompositionPerc();
        uint allApr;
        uint poolCnt = _tokens.length;
        for (uint i = 0; i < poolCnt; i ++) {
            address token = _tokens[i];
            if (token == address(WAVAX)) {
                allApr += WAVAXVault.getAPR() * perc[i];
            }
        }
        return (allApr / DENOMINATOR);
    }
}

contracts/stVaults/lido/MoonbeamStDOTVau.content:
//SPDX-License-Identifier: MIT
//
///@notice The MoonbeamStDOTVault contract stakes xcDOT tokens into stDOT on Moonbeam.
///@dev https://docs.polkadot.lido.fi/fundamentals/liquid-staking
//
pragma solidity  0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "../BasicStVault.sol";
import "../../bni/constant/MoonbeamConstant.sol";

interface IStDOT {
    function getUnbonded(address _holder) external view returns (uint waiting, uint unbonded);
    function deposit(uint _amount) external returns (uint);
    function redeem(uint _amount) external;
    function claimUnbonded() external;
    function getSharesByPooledKSM(uint _amount) external view returns (uint);
    function getPooledKSMByShares(uint _sharesAmount) external view returns (uint);
}

contract MoonbeamStDOTVault is BasicStVault {
    using SafeERC20Upgradeable for IERC20Upgradeable;

    function initialize1(
        address _treasury, address _admin,
        address _priceOracle
    ) public initializer {
        super.initialize(
            "STI Staking xcDOT", "stiStDOT",
            _treasury, _admin,
            _priceOracle,
            MoonbeamConstant.xcDOT,
            MoonbeamConstant.stDOT
        );

        unbondingPeriod = 30 days;
        oneEpoch = 24 hours;

        token.safeApprove(address(stToken), type(uint).max);
    }

    function _invest(uint _amount) internal override returns (uint _invested) {
        IStDOT(address(stToken)).deposit(_amount);
        return _amount;
    }

    function _redeem(uint _stAmount) internal override returns (uint _redeemed) {
        // Because _stAmount-stBalance may be a calculation delta in withdraw function,
        // it will reduce the pendingRedeems even though no redeeming on the staking pool.
        _redeemed = _stAmount;

        uint stBalance = stToken.balanceOf(address(this));
        IStDOT(address(stToken)).redeem(_stAmount > stBalance ? stBalance : _stAmount);
    }

    function _claimUnbonded() internal override {
        IStDOT(address(stToken)).claimUnbonded();

        uint _emergencyUnbondings = emergencyUnbondings;
        if (_emergencyUnbondings > 0 && paused()) {
            (uint unbondings,) = IStDOT(address(stToken)).getUnbonded(address(this));
            if (_emergencyUnbondings > unbondings) {
                emergencyUnbondings = unbondings;
            }
        }
    }

    function _emergencyWithdraw(uint _pendingRedeems) internal override returns (uint _redeemed) {
        uint stBalance = stToken.balanceOf(address(this));
        if (stBalance >= minRedeemAmount) {
            IStDOT(address(stToken)).redeem(stBalance);
            emergencyUnbondings = (stBalance > _pendingRedeems) ? stBalance - _pendingRedeems : 0;
            _redeemed = stBalance;
        }
    }

    ///@param _amount Amount of tokens
    function getStTokenByPooledToken(uint _amount) public override view returns(uint) {
        return _amount;
        // return IStDOT(address(stToken)).getSharesByPooledKSM(_amount);
    }

    ///@param _stAmount Amount of stTokens
    function getPooledTokenByStToken(uint _stAmount) public override view returns(uint) {
        return _stAmount;
        // return IStDOT(address(stToken)).getPooledKSMByShares(_stAmount);
    }

    function getTokenUnbonded() public override view returns (uint _amount) {
        (, _amount) = IStDOT(address(stToken)).getUnbonded(address(this));
    }
}

