//SPDX-License-Identifier: MIT
pragma solidity  0.8.9;

interface IChainlink {
    function latestAnswer() external view returns (int256);
}

library PriceLib {

    address internal constant USDT = 0x55d398326f99059fF775485246999027B3197955;
    address internal constant USDC = 0x8AC76a51cc950d9822D68b83fE1Ad97B32Cd580d;
    address internal constant BUSD = 0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56;

    /// @return the price in USD of 8 decimals in precision.
    function getAssetPrice(address asset) internal view returns (uint) {
        if (asset == USDT) {
            return uint(IChainlink(0xB97Ad0E74fa7d920791E90258A6E2085088b4320).latestAnswer());
        } else if (asset == USDC) {
            return uint(IChainlink(0x51597f405303C4377E36123cBc172b13269EA163).latestAnswer());
        } else if (asset == BUSD) {
            return uint(IChainlink(0xcBb98864Ef56E9042e7d2efef76141f15731B82f).latestAnswer());
        }
        return 0;
    }

    function getBNBPriceInUSD() internal view returns (uint, uint) {
        uint BNBPriceInUSD = uint(IChainlink(0x0567F2323251f0Aab15c8dFb1967E4e8A7D42aeE).latestAnswer()); // 8 decimals
        return (BNBPriceInUSD, 1e8);
    }

    function getCAKEPriceInUSD() internal view returns (uint, uint) {
        uint CAKEPriceInUSD = uint(IChainlink(0xB6064eD41d4f67e353768aA239cA86f4F73665a1).latestAnswer()); // 8 decimals
        return (CAKEPriceInUSD, 1e8);
    }

    function getUSDTPriceInUSD() internal view returns (uint, uint) {
        uint USDTPriceInUSD = uint(IChainlink(0xB97Ad0E74fa7d920791E90258A6E2085088b4320).latestAnswer()); // 8 decimals
        return (USDTPriceInUSD, 1e8);
    }
}

contracts/lci/LCIStrategy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "./libs/Price.sol";

interface IRouter {
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);

    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);

    function getAmountsOut(uint amountIn, address[] memory path) external view returns (uint[] memory amounts);
}

interface IL2Vault is IERC20Upgradeable {
    function deposit(uint amount) external;
    function withdraw(uint share) external;
    function getAllPoolInUSD() external view returns (uint);
    function getAPR() external view returns (uint);
}

contract LCIStrategy is OwnableUpgradeable {
    using SafeERC20Upgradeable for IERC20Upgradeable;

    IERC20Upgradeable public constant USDT = IERC20Upgradeable(0x55d398326f99059fF775485246999027B3197955);
    IERC20Upgradeable public constant USDC = IERC20Upgradeable(0x8AC76a51cc950d9822D68b83fE1Ad97B32Cd580d);
    IERC20Upgradeable public constant BUSD = IERC20Upgradeable(0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56);

    IERC20Upgradeable public constant USDTUSDC = IERC20Upgradeable(0xEc6557348085Aa57C72514D67070dC863C0a5A8c);
    IERC20Upgradeable public constant USDTBUSD = IERC20Upgradeable(0x7EFaEf62fDdCCa950418312c6C91Aef321375A00);
    IERC20Upgradeable public constant USDCBUSD = IERC20Upgradeable(0x2354ef4DF11afacb85a5C7f98B624072ECcddbB1);

    IRouter public constant PnckRouter = IRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);

    uint constant POOL_COUNT = 3;
    IL2Vault public USDTUSDCVault;
    IL2Vault public USDTBUSDVault;
    IL2Vault public USDCBUSDVault;

    uint constant DENOMINATOR = 10000;
    uint[] public targetPercentages;

    address public vault;

    event TargetComposition (uint USDTUSDCTargetPool, uint USDTBUSDTargetPool, uint USDCBUSDTargetPool);
    event CurrentComposition (uint USDTUSDCTargetPool, uint USDTBUSDTargetPool, uint USDCBUSDCurrentPool);
    event InvestUSDTUSDC(uint USDTAmt, uint USDTUSDCAmt);
    event InvestUSDTBUSD(uint USDTAmt, uint USDTBUSDAmt);
    event InvestUSDCBUSD(uint USDTAmt, uint USDCBUSDAmt);
    event Withdraw(uint sharePerc, uint USDTAmt);
    event WithdrawUSDTUSDC(uint lpTokenAmt, uint USDTAmt);
    event WithdrawUSDTBUSD(uint lpTokenAmt, uint USDTAmt);
    event WithdrawUSDCBUSD(uint lpTokenAmt, uint USDTAmt);
    event EmergencyWithdraw(uint USDTAmt);

    modifier onlyVault {
        require(msg.sender == vault, "Only vault");
        _;
    }

    function initialize(IL2Vault _USDTUSDCVault, IL2Vault _USDTBUSDVault, IL2Vault _USDCBUSDVault) external initializer {
        __Ownable_init();

        targetPercentages.push(6000); // 60%
        targetPercentages.push(2000); // 20%
        targetPercentages.push(2000); // 20%

        USDTUSDCVault = _USDTUSDCVault;
        USDTBUSDVault = _USDTBUSDVault;
        USDCBUSDVault = _USDCBUSDVault;

        USDT.safeApprove(address(PnckRouter), type(uint).max);
        USDC.safeApprove(address(PnckRouter), type(uint).max);
        BUSD.safeApprove(address(PnckRouter), type(uint).max);

        USDTUSDC.safeApprove(address(USDTUSDCVault), type(uint).max);
        USDTBUSD.safeApprove(address(USDTBUSDVault), type(uint).max);
        USDCBUSD.safeApprove(address(USDCBUSDVault), type(uint).max);

        USDTUSDC.safeApprove(address(PnckRouter), type(uint).max);
        USDTBUSD.safeApprove(address(PnckRouter), type(uint).max);
        USDCBUSD.safeApprove(address(PnckRouter), type(uint).max);
    }

    function invest(uint USDTAmt) external onlyVault {
        USDT.safeTransferFrom(vault, address(this), USDTAmt);
        USDTAmt = USDT.balanceOf(address(this));
        (uint USDTPriceInUSD, uint denominator) = PriceLib.getUSDTPriceInUSD();

        uint[] memory pools = getEachPoolInUSD();
        uint allPool = pools[0] + pools[1] + pools[2] + USDTAmt * USDTPriceInUSD / denominator; // USDT's decimals is 18

        uint totalAllocation;
        uint[] memory allocations = new uint[](POOL_COUNT);
        for (uint i = 0; i < POOL_COUNT; i ++) {
            uint target = allPool * targetPercentages[i] / DENOMINATOR;
            if (pools[i] < target) {
                uint diff = target - pools[i];
                allocations[i] = diff;
                totalAllocation += diff;
            }
        }

        uint[] memory USDTAmts = new uint[](POOL_COUNT);
        for (uint i = 0; i < POOL_COUNT; i ++) {
            USDTAmts[i] = USDTAmt * allocations[i] / totalAllocation;
        }

        if (USDTAmts[0] > 0) {
            _investUSDTUSDC(USDTAmts[0]);
        }
        if (USDTAmts[1] > 0) {
            _investUSDTBUSD(USDTAmts[1]);
        }
        if (USDTAmts[2] > 0) {
            _investUSDCBUSD(USDT.balanceOf(address(this)));
        }

        emit CurrentComposition(pools[0], pools[1], pools[2]);
        emit TargetComposition(targetPercentages[0], targetPercentages[1], targetPercentages[2]);
    }


    function _investUSDTUSDC(uint _usdtAmt) private {
        uint _amt = _usdtAmt/2;
        _swap(address(USDT), address(USDC), _amt, _amt*98/100);

        uint _USDCAmt = USDC.balanceOf(address(this));
        uint lpTokens = _addLiquidity(address(USDT), address(USDC), _amt, _USDCAmt);

        USDTUSDCVault.deposit(lpTokens);
        emit InvestUSDTUSDC(_usdtAmt, lpTokens);
    }

    function _investUSDTBUSD(uint _usdtAmt) private {
        uint _amt = _usdtAmt / 2 ;
        _swap(address(USDT), address(BUSD), _amt, _amt*98/100);

        uint _BUSDAmt = BUSD.balanceOf(address(this));
        uint lpTokens = _addLiquidity(address(USDT), address(BUSD), _amt, _BUSDAmt);

        USDTBUSDVault.deposit(lpTokens);
        emit InvestUSDTBUSD(_usdtAmt, lpTokens);
    }

    function _investUSDCBUSD(uint _usdtAmt) private {
        uint _amt = _usdtAmt / 2 ;
        _swap(address(USDT), address(USDC), _amt, _amt*98/100);
        _swap(address(USDT), address(BUSD), _amt, _amt*98/100);

        uint _USDCAmt = USDC.balanceOf(address(this));
        uint _BUSDAmt = BUSD.balanceOf(address(this));

        uint lpTokens = _addLiquidity(address(USDC), address(BUSD), _USDCAmt, _BUSDAmt);

        USDCBUSDVault.deposit(lpTokens);
        emit InvestUSDCBUSD(_usdtAmt, lpTokens);
    }

    function withdrawPerc(uint sharePerc) external onlyVault returns (uint USDTAmt) {
        require(sharePerc <= 1e18, "Over 100%");

        uint USDTAmtBefore = USDT.balanceOf(address(this));
        _withdrawUSDTUSDC(sharePerc);
        _withdrawUSDTBUSD(sharePerc);
        _withdrawUSDCBUSD(sharePerc);
        USDTAmt = USDT.balanceOf(address(this)) - USDTAmtBefore;
        USDT.safeTransfer(vault, USDTAmt);

        emit Withdraw(sharePerc, USDTAmt);
    }

    function _withdrawUSDTUSDC(uint _sharePerc) private {
        uint amount = USDTUSDCVault.balanceOf(address(this)) * _sharePerc / 1e18;
        if (0 < amount) {
            USDTUSDCVault.withdraw(amount);

            uint _amt = USDTUSDC.balanceOf(address(this));
            (uint _amtUSDT,) = _removeLiquidity(address(USDT), address(USDC), _amt);
            uint _amtUSDC = USDC.balanceOf(address(this));
            _amtUSDT += _swap(address(USDC), address(USDT), _amtUSDC, _amtUSDC*98/100);

            emit WithdrawUSDTUSDC(_amt, _amtUSDT);
        }
    }

    function _withdrawUSDTBUSD(uint _sharePerc) private {
        uint amount = USDTBUSDVault.balanceOf(address(this)) * _sharePerc / 1e18;
        if (0 < amount) {
            USDTBUSDVault.withdraw(amount);

            uint _amt = USDTBUSD.balanceOf(address(this));
            (uint _amtUSDT,) = _removeLiquidity(address(USDT), address(BUSD), _amt);
            uint _amtBUSD = BUSD.balanceOf(address(this));
            _amtUSDT += _swap(address(BUSD), address(USDT), _amtBUSD, _amtBUSD*98/100);

            emit WithdrawUSDTBUSD(_amt, _amtUSDT);
        }
    }

    function _withdrawUSDCBUSD(uint _sharePerc) private {
        uint amount = USDCBUSDVault.balanceOf(address(this)) * _sharePerc / 1e18;
        if (0 < amount) {
            USDCBUSDVault.withdraw(amount);

            uint _amt = USDCBUSD.balanceOf(address(this));
            _removeLiquidity(address(USDC), address(BUSD), _amt);
            uint _amtUSDC = USDC.balanceOf(address(this));
            uint _amtBUSD = BUSD.balanceOf(address(this));
            uint _usdtAmt = _swap(address(USDC), address(USDT), _amtUSDC, _amtUSDC*98/100);
            _usdtAmt += _swap(address(BUSD), address(USDT), _amtBUSD, _amtBUSD*98/100);

            emit WithdrawUSDCBUSD(_amt, _usdtAmt);
        }
    }

    function _swap(address _tokenA, address _tokenB, uint _amt, uint _minAmount) private returns (uint) {
        address[] memory path = new address[](2);
        path[0] = _tokenA;
        path[1] = _tokenB;
        return (PnckRouter.swapExactTokensForTokens(_amt , _minAmount, path, address(this), block.timestamp))[1];
    }

    function _addLiquidity(address _tokenA, address _tokenB, uint _amtA, uint _amtB) private returns (uint liquidity) {
        (,,liquidity) = PnckRouter.addLiquidity(_tokenA, _tokenB, _amtA, _amtB, 0, 0, address(this), block.timestamp);
    }

    function _removeLiquidity(address _tokenA, address _tokenB, uint _amt) private returns (uint _amtA, uint _amtB) {
        (_amtA, _amtB) = PnckRouter.removeLiquidity(_tokenA, _tokenB, _amt, 0, 0, address(this), block.timestamp);
    }

    function withdrawFromFarm(uint farmIndex, uint sharePerc) external onlyVault returns (uint USDTAmt) {
        require(sharePerc <= 1e18, "Over 100%");
        if (farmIndex == 0) _withdrawUSDTUSDC(sharePerc); 
        else if (farmIndex == 1) _withdrawUSDTBUSD(sharePerc);
        else if (farmIndex == 2) _withdrawUSDCBUSD(sharePerc);
        USDTAmt = USDT.balanceOf(address(this));
        USDT.safeTransfer(vault, USDTAmt);
    }

    function emergencyWithdraw() external onlyVault {
        // 1e18 == 100% of share
        _withdrawUSDTUSDC(1e18);
        _withdrawUSDTBUSD(1e18);
        _withdrawUSDCBUSD(1e18);
        uint USDTAmt = USDT.balanceOf(address(this));
        if (0 < USDTAmt) {
            USDT.safeTransfer(vault, USDTAmt);
        }
        emit EmergencyWithdraw(USDTAmt);
    }

    function setVault(address _vault) external onlyOwner {
        require(vault == address(0), "Vault set");
        vault = _vault;
    }

    function setLPCompositionTargetPerc(uint[] calldata _targetPerc) external onlyOwner {
        uint targetCnt = _targetPerc.length;
        require(targetCnt == targetPercentages.length, "Invalid count");

        uint sum;
        for (uint i = 0; i < targetCnt; i ++) {
            targetPercentages[i] = _targetPerc[i];
            sum += _targetPerc[i];
        }
        require(sum == DENOMINATOR, "Invalid parameter");
    }

    function getUSDTUSDCPoolInUSD() private view  returns (uint) {
        uint amt = USDTUSDCVault.getAllPoolInUSD();
        return amt == 0 ? 0 : amt * USDTUSDCVault.balanceOf(address(this)) / USDTUSDCVault.totalSupply(); //to exclude L1 deposits from other addresses
    }

    function getUSDTBUSDPoolInUSD() private view returns (uint) {
        uint amt = USDTBUSDVault.getAllPoolInUSD();
        return amt == 0 ? 0 : amt * USDTBUSDVault.balanceOf(address(this)) / USDTBUSDVault.totalSupply();
    }

    function getUSDCBUSDPoolInUSD() private view returns (uint) {
        uint amt = USDCBUSDVault.getAllPoolInUSD();
        return amt == 0 ? 0 : amt * USDCBUSDVault.balanceOf(address(this)) / USDCBUSDVault.totalSupply();
    }

    function getEachPoolInUSD() private view returns (uint[] memory pools) {
        pools = new uint[](POOL_COUNT);
        pools[0] = getUSDTUSDCPoolInUSD();
        pools[1] = getUSDTBUSDPoolInUSD();
        pools[2] = getUSDCBUSDPoolInUSD();
    }

    function getAllPoolInUSD() public view returns (uint) {
        uint[] memory pools = getEachPoolInUSD();
        return pools[0] + pools[1] + pools[2];
    }

    function getCurrentLPCompositionPerc() public view returns (uint[] memory percentages) {
        uint[] memory pools = getEachPoolInUSD();
        uint allPool = pools[0] + pools[1] + pools[2];
        percentages = new uint[](POOL_COUNT);
        for (uint i = 0; i < POOL_COUNT; i ++) {
            percentages[i] = allPool == 0 ? targetPercentages[i] : pools[i] * DENOMINATOR / allPool;
        }
    }

    function getCurrentTokenCompositionPerc() external view returns (address[] memory tokens, uint[] memory percentages) {
        uint[] memory lpPerc = getCurrentLPCompositionPerc();
        tokens = new address[](POOL_COUNT);
        tokens[0] = address(USDT);
        tokens[1] = address(USDC);
        tokens[2] = address(BUSD);
        percentages = new uint[](POOL_COUNT);
        percentages[0] = (lpPerc[0] + lpPerc[1]) / 2;
        percentages[1] = (lpPerc[0] + lpPerc[2]) / 2;
        percentages[2] = (lpPerc[1] + lpPerc[2]) / 2;
    }

    function getAPR() external view returns (uint) {
        uint[] memory lpPerc = getCurrentLPCompositionPerc();
        uint allApr = USDTUSDCVault.getAPR() * lpPerc[0]
                    + USDTBUSDVault.getAPR() * lpPerc[1]
                    + USDCBUSDVault.getAPR() * lpPerc[2];
        return (allApr / DENOMINATOR);
    }

}

contracts/lci/deps/VaultTest.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity  0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "../libs/Math.sol";
import "../libs/Price.sol";

interface IUniRouter {
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity) ;

    function getAmountsOut(uint amountIn, address[] memory path) external view returns (uint[] memory amounts);

}

interface IUniPair is IERC20Upgradeable{
    function getReserves() external view returns (uint, uint);
    function token0() external view returns (address);
    function token1() external view returns (address);
}

interface IMasterChefV2 {
    function poolInfo(
        uint pid
    ) external view returns(
        uint accCakePerShare, uint lastRewardBlock, uint allocPoint, uint totalBoostedShare, bool isRegular
    );

    function userInfo(
        uint pid, address user
    ) external view returns(
        uint amount, uint rewardDebt, uint boostMultiplier
    );

    function pendingCake(uint pid, address user) external view returns (uint);
    function lpToken(uint pid) external view returns (address);
    function totalRegularAllocPoint() external view returns (uint);
    function totalSpecialAllocPoint() external view returns (uint);
    function cakePerBlock(bool isRegular) external view returns (uint amount);

    function deposit(uint pid, uint amount) external;
    function withdraw(uint pid, uint amount) external;
}

contract BscVaultTest is Initializable, ERC20Upgradeable, OwnableUpgradeable, PausableUpgradeable, ReentrancyGuardUpgradeable{
    using SafeERC20Upgradeable for IERC20Upgradeable;

    using SafeERC20Upgradeable for IUniPair;

    IERC20Upgradeable public constant CAKE  = IERC20Upgradeable(0x0E09FaBB73Bd3Ade0a17ECC321fD13a19e81cE82);
    IERC20Upgradeable public constant WBNB = IERC20Upgradeable(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);

    IUniRouter public constant PckRouter = IUniRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    IMasterChefV2 public constant MasterChefV2 = IMasterChefV2(0xa5f8C5Dbd5F286960b9d90548680aE5ebFf07652);

    uint constant DENOMINATOR = 10000;
    uint public yieldFee;

    uint public pid;
    IUniPair public lpToken;
    IERC20Upgradeable public token0;
    IERC20Upgradeable public token1;

    address public treasuryWallet;
    address public admin;

    mapping(address => uint) private depositedBlock;

    uint constant DAY_IN_SEC = 86400; // 3600 * 24
    uint constant YEAR_IN_SEC = 365 * DAY_IN_SEC;
    uint constant BSC_BLOCK_TIME = 3;
    uint constant BLOCKS_PER_YEAR = (60 / BSC_BLOCK_TIME) * 60 * 24 * 365; // 10512000

    uint public lpRewardApr;
    uint public lpReservePerShare;
    uint public lpDataLastUpdate;

    event Deposit(address _user, uint _amount, uint _shares);
    event EmergencyWithdraw(uint _amount);
    event Invest(uint _amount);
    event SetAdmin(address _oldAdmin, address _newAdmin);
    event SetYieldFeePerc(uint _fee);
    event SetTreasuryWallet(address _wallet);
    event Withdraw(address _user, uint _amount, uint _shares);
    event YieldFee(uint _amount);
    event Yield(uint _amount);

    modifier onlyOwnerOrAdmin {
        require(msg.sender == owner() || msg.sender == admin, "Only owner or admin");
        _;
    }

    function initialize(string memory _name, string memory _symbol, 
        uint _pid,
        address _treasury, address _admin
    ) external initializer {

        __ERC20_init(_name, _symbol);
        __Ownable_init();

        yieldFee = 2000; //20%
        pid = _pid;

        // address _lpToken = MasterChefV2.lpToken(_pid);

        // lpToken = IUniPair(_lpToken);
        // token0 = IERC20Upgradeable(lpToken.token0());
        // token1 = IERC20Upgradeable(lpToken.token1());

        treasuryWallet = _treasury;
        admin = _admin;

        // lpToken.safeApprove(address(MasterChefV2), type(uint).max);
        // CAKE.safeApprove(address(PckRouter), type(uint).max);
        // token0.approve(address(PckRouter), type(uint).max);
        // token1.approve(address(PckRouter), type(uint).max);

        _updateLpRewardApr();
    }

    /**
     *@param _amount amount of lptokens to deposit
    */
    function deposit(uint _amount) external nonReentrant whenNotPaused{
        // require(_amount > 0, "Invalid amount");

        // depositedBlock[msg.sender] = block.number;
        // lpToken.safeTransferFrom(msg.sender, address(this), _amount);

        // uint _pool = getAllPool();
        // MasterChefV2.deposit(pid, _amount);

        // uint _totalSupply = totalSupply();
        // uint _shares = _totalSupply == 0 ? _amount : _amount * _totalSupply / _pool;
        // _mint(msg.sender, _shares);

        // emit Deposit(msg.sender, _amount, _shares);
    }

    /**
     *@param _shares amount of shares to burn
    */
    function withdraw(uint _shares) external nonReentrant{
        // require(_shares > 0, "Invalid Amount");
        // require(balanceOf(msg.sender) >= _shares, "Not enough balance");
        // require(depositedBlock[msg.sender] != block.number, "Withdraw within same block");

        // uint _amountToWithdraw = getAllPool() * _shares / totalSupply(); 

        // uint lpTokenAvailable = lpToken.balanceOf(address(this));
        // if(lpTokenAvailable < _amountToWithdraw) {
        //     MasterChefV2.withdraw(pid, _amountToWithdraw - lpTokenAvailable );
        // }
        // _burn(msg.sender, _shares);

        // lpToken.safeTransfer(msg.sender, _amountToWithdraw);
        // emit Withdraw(msg.sender, _amountToWithdraw, _shares);
    }

    function _invest() private returns (uint available){
        available = lpToken.balanceOf(address(this));
        if(available > 0) {
            MasterChefV2.deposit(pid, available);
        }
    }

    ///@notice Withdraws funds staked in mirror to this vault and pauses deposit, yield, invest functions
    function emergencyWithdraw() external onlyOwnerOrAdmin whenNotPaused{ 
        _pause();
        // _yield();
        // (uint stakedTokens,,) = MasterChefV2.userInfo(pid, address(this));
        // if(stakedTokens > 0 ) {
        //     MasterChefV2.withdraw(pid, stakedTokens);
        // }
        // emit EmergencyWithdraw(stakedTokens);
    }

    ///@notice Unpauses deposit, yield, invest functions, and invests funds.
    function reinvest() external onlyOwnerOrAdmin whenPaused {
        _unpause();
        // _invest();
    }

    function setAdmin(address _newAdmin) external onlyOwner{
        address oldAdmin = admin;
        admin = _newAdmin;

        emit SetAdmin(oldAdmin, _newAdmin);
    }

    ///@notice Function to set deposit and yield fee
    ///@param _yieldFeePerc deposit fee percentage. 2000 for 20%
    function setFee(uint _yieldFeePerc) external onlyOwner{
        require(_yieldFeePerc < DENOMINATOR, "yieldFeePerc invalid");
        yieldFee = _yieldFeePerc;
        emit SetYieldFeePerc(_yieldFeePerc);
    }

    function setTreasuryWallet(address _wallet) external onlyOwner {
        require(_wallet != address(0), "wallet invalid");
        treasuryWallet = _wallet;
        emit SetTreasuryWallet(_wallet);
    }

    function yield() external onlyOwnerOrAdmin whenNotPaused {
        // _yield();
        _updateLpRewardApr();
    }

    function _yield() private {
        uint cakeBalance = CAKE.balanceOf(address(this));

        if(cakeBalance > 0) {
            uint fee = cakeBalance * yieldFee / DENOMINATOR; //yield fee
            CAKE.safeTransfer(treasuryWallet, fee);
            cakeBalance -= fee;

            uint _token0Amount = token0 == CAKE ? cakeBalance /2 : _swap(address(CAKE), address(token0), cakeBalance/2)[1];
            uint _token1Amount = token1 == CAKE ? cakeBalance /2 :  _swap(address(CAKE), address(token1), cakeBalance/2)[1];

            PckRouter.addLiquidity(address(token0), address(token1), _token0Amount, _token1Amount, 0, 0, address(this), block.timestamp);

            _invest();

            (uint CAKEPriceInUSD, uint denominator) = PriceLib.getCAKEPriceInUSD();
            emit Yield((cakeBalance + fee) * CAKEPriceInUSD / denominator);
            emit YieldFee(fee * CAKEPriceInUSD / denominator);
        }
    }

    function _swap(address _tokenA, address _tokenB, uint _amt) private returns (uint[] memory amounts){
        address[] memory path = new address[](2);

        path[0] = address(_tokenA);
        path[1] = address(_tokenB);

        amounts = PckRouter.swapExactTokensForTokens(_amt, 0, path, address(this), block.timestamp);
    }

    function getAllPool() public view returns (uint ) {
        // (uint stakedTokens,,) = MasterChefV2.userInfo(pid, address(this));
        // return lpToken.balanceOf(address(this)) + stakedTokens;
        return 0;
    }

    function getAllPoolInBNB() public view returns (uint _valueInBNB) {
        // return _getValueInBNB(getAllPool());
        return 0;
    }

    function _getValueInBNB(uint lpAmt) public view returns (uint _valueInBNB) {
        uint _totalSupply = lpToken.totalSupply();

        (uint _reserve0, uint _reserve1) = lpToken.getReserves();

        uint _total0 = lpAmt * _reserve0 / _totalSupply;
        uint _total1 = lpAmt * _reserve1 / _totalSupply;

        _valueInBNB = (_total0 * _getPriceInBNB(address(token0))) + 
        (_total1 * _getPriceInBNB(address(token1))) ;

        _valueInBNB = _valueInBNB / 1e18;
    }

    function _getPriceInBNB(address _token) private view returns (uint) {
        if(_token == address(WBNB)) {
            return 1e18;
        } else {
            address[] memory path = new address[](2);

            path[0] = _token;
            path[1] = address(WBNB);
            return PckRouter.getAmountsOut(1e18, path)[1];
        }
    }

    function getAllPoolInUSD() public view returns (uint) {
        // (uint BNBPriceInUSD, uint denominator) = PriceLib.getBNBPriceInUSD();
        // return getAllPoolInBNB() * BNBPriceInUSD / denominator;
        return 0;
    }

    function getPricePerFullShare(bool inUSD) external view returns (uint) {
        // uint _totalSupply = totalSupply();
        // if (_totalSupply == 0) return 1e18;
        // return inUSD == true ?
        //     getAllPoolInUSD() * 1e18 / _totalSupply :
        //     getAllPool() * 1e18 / _totalSupply;
        return 1e18;
    }

    ///@notice Returns the pending rewards in UDS.
    function getPendingRewards() public view returns (uint) {
        // uint pendingCake = MasterChefV2.pendingCake(pid, address(this));
        // (uint CAKEPriceInUSD, uint denominator) = PriceLib.getCAKEPriceInUSD();
        // return pendingCake * CAKEPriceInUSD / denominator;
        return 10e18;
    }

    function getAPR() external view returns (uint) {
        (uint _lpRewardApr,,) = getLpRewardApr();
        uint _farmRewardApr = getCakeRewardApr();
        _farmRewardApr = _farmRewardApr * (DENOMINATOR-yieldFee) / DENOMINATOR;
        return (_lpRewardApr + _farmRewardApr);
    }

    function resetLpRewardApr() external onlyOwner {
        lpRewardApr = 0;
        lpReservePerShare = 0;
        lpDataLastUpdate = 0;
        _updateLpRewardApr();
    }

    function _updateLpRewardApr() private {
        (uint _lpRewardApr, uint _lpReservePerShare, bool _update) = getLpRewardApr();
        if (_update) {
            lpRewardApr = _lpRewardApr;
            lpReservePerShare = _lpReservePerShare;
            lpDataLastUpdate = block.timestamp;
        }
    }

    function _getLpReservePerShare() private view returns (uint) {
        uint _totalSupply = lpToken.totalSupply();
        if (_totalSupply == 0) return 0;
        (uint reserve0, uint reserve1) = lpToken.getReserves();
        return Math.sqrt(reserve0 * reserve1) *1e18 / _totalSupply;
    }

    function getLpRewardApr() public view returns (uint, uint, bool) {
        // if (lpRewardApr == 0 || (lpDataLastUpdate+DAY_IN_SEC) <= block.timestamp) {
        //     uint _lpReservePerShare = _getLpReservePerShare();
        //     if (0 < lpReservePerShare && lpReservePerShare < _lpReservePerShare) {
        //         uint _lpRewardApr = (_lpReservePerShare-lpReservePerShare) * YEAR_IN_SEC * 1e18 / (lpReservePerShare * (block.timestamp-lpDataLastUpdate));
        //         return (_lpRewardApr, _lpReservePerShare, true);
        //     } else {
        //         return (0, _lpReservePerShare, true);
        //     }
        // } else {
        //     return (lpRewardApr, lpReservePerShare, false);
        // }
        return (0, 0, false);
    }

    function getCakeRewardApr() public view returns (uint) {
        // uint yearlyCakeReward = _getYearlyCakeReward();
        // (uint CAKEPriceInUSD, uint cakeDenominator) = PriceLib.getCAKEPriceInUSD();
        // uint yearlyRewardInUSD = yearlyCakeReward * CAKEPriceInUSD / cakeDenominator;

        // uint poolInBNB = _getValueInBNB(lpToken.balanceOf(address(MasterChefV2)));
        // (uint BNBPriceInUSD, uint bnbDenominator) = PriceLib.getBNBPriceInUSD();
        // uint poolInUSD = poolInBNB * BNBPriceInUSD / bnbDenominator;

        // return yearlyRewardInUSD * 1e18 / poolInUSD;
        return 7e15;
    }

    function _getYearlyCakeReward() private view returns (uint) {
        (,, uint allocPoint, , bool isRegular) = MasterChefV2.poolInfo(pid);
        uint totalAllocPoint = isRegular ? MasterChefV2.totalRegularAllocPoint() : MasterChefV2.totalSpecialAllocPoint();
        uint cakePerBlock = MasterChefV2.cakePerBlock(isRegular);
        return cakePerBlock * BLOCKS_PER_YEAR * allocPoint / totalAllocPoint;
    }

}

